/**
 * –§—É–Ω–∫—Ü–∏—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è FAQ —ç–ª–µ–º–µ–Ω—Ç–∞
 */
function toggleFAQ(index) {
    const allItems = document.querySelectorAll('.faq-item');
    const currentItem = allItems[index];
    const answer = currentItem.querySelector('.faq-answer');
    const icon = currentItem.querySelector('.faq-icon');
    const isActive = currentItem.classList.contains('active');

    // –ó–∞–∫—Ä—ã—Ç—å –≤—Å–µ –æ—Å—Ç–∞–ª—å–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
    allItems.forEach((item, i) => {
        if (i !== index) {
            item.classList.remove('active');
            item.querySelector('.faq-answer').style.maxHeight = null;
            item.querySelector('.faq-icon').style.transform = 'rotate(0deg)';
        }
    });

    // –ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å —Ç–µ–∫—É—â–∏–π —ç–ª–µ–º–µ–Ω—Ç
    if (isActive) {
        currentItem.classList.remove('active');
        answer.style.maxHeight = null;
        icon.style.transform = 'rotate(0deg)';
    } else {
        currentItem.classList.add('active');
        answer.style.maxHeight = answer.scrollHeight + 'px';
        icon.style.transform = 'rotate(180deg)';
    }
}

/**
 * –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FAQ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
 */
document.addEventListener('DOMContentLoaded', function() {
    const faqContainer = document.querySelector('.faq-container');

    if (!faqContainer) {
        console.error('FAQ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä –Ω–µ –Ω–∞–π–¥–µ–Ω');
        return;
    }

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è FAQ —ç–ª–µ–º–µ–Ω—Ç–æ–≤
    faqData.forEach((item, index) => {
        const faqItem = document.createElement('div');
        faqItem.className = 'faq-item';
        faqItem.setAttribute('itemscope', '');
        faqItem.setAttribute('itemprop', 'mainEntity');
        faqItem.setAttribute('itemtype', 'https://schema.org/Question');

        faqItem.innerHTML = `
            <div class="faq-question" onclick="toggleFAQ(${index})">
                <span class="faq-question-text" itemprop="name">${item.question}</span>
                <span class="faq-icon">‚ñº</span>
            </div>
            <div class="faq-answer" itemscope itemprop="acceptedAnswer" itemtype="https://schema.org/Answer">
                <div itemprop="text">
                    <div class="faq-answer-short">${item.shortAnswer}</div>
                    <div class="faq-answer-full">${item.fullAnswer}</div>
                    <div class="faq-cta faq-cta-clickable" onclick="openCallbackModal(); event.stopPropagation();">
                        <strong>üí° –ß—Ç–æ –¥–µ–ª–∞—Ç—å –¥–∞–ª—å—à–µ:</strong><br>
                        ${item.cta}
                        <br><span class="faq-cta-hint">üëÜ –ù–∞–∂–º–∏—Ç–µ, —á—Ç–æ–±—ã –æ—Å—Ç–∞–≤–∏—Ç—å –∑–∞—è–≤–∫—É</span>
                    </div>
                </div>
            </div>
        `;

        faqContainer.appendChild(faqItem);
    });

    // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ JSON-LD —Ä–∞–∑–º–µ—Ç–∫–∏ –¥–ª—è FAQ
    const faqJsonLd = {
        "@context": "https://schema.org",
        "@type": "FAQPage",
        "mainEntity": faqData.map(item => ({
            "@type": "Question",
            "name": item.question,
            "acceptedAnswer": {
                "@type": "Answer",
                "text": `${item.shortAnswer} ${item.fullAnswer}`
            }
        }))
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.text = JSON.stringify(faqJsonLd);
    document.head.appendChild(script);

    console.log('FAQ –¥–ª—è —Å—Ç—Ä–∞–Ω–∏—Ü—ã –∞–ª–º–∞–∑–Ω–æ–≥–æ –±—É—Ä–µ–Ω–∏—è —É—Å–ø–µ—à–Ω–æ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω: ' + faqData.length + ' –≤–æ–ø—Ä–æ—Å–æ–≤');
});
